id.map:
function (doc) {
  if (doc._id.match(/^geotagged/)) {
    for (var page in doc.annotations) {
      for (var line in doc.annotations[page]) {
        for (var i in doc.annotations[page][line]) {
          if (doc.annotations[page][line][i].uber_h3) {
            for (var j in doc.annotations[page][line][i].uber_h3) {
              emit([doc.annotations[page][line][i].uber_h3[j], doc.bhl_id, page],1);
            }
          }
        }
      }
    }
  }
}
