item-layout.map:
function (doc) {
  if (doc._id.match(/^layout/) && doc.bhl_item) {
    emit("item/" + doc.bhl_item, doc._id);
  }
}

item-parts.map:
function (doc) {
  if (doc._id.match(/^part/)) {
    for (var j in doc.isPartOf) {
      if (typeof (doc.isPartOf[j]) === 'string') {
        if (doc.isPartOf[j].match(/^item/)) {
          emit(doc.isPartOf[j], doc._id);
        }
      }
    }
  }
}

title-ia.map:
function (doc) {
  if (doc._id.match(/^item/)) {
    var TitleID = doc.isPartOf.replace(/bibliography\//, '');
    var IA = doc.sameAs.replace(/https:\/\/archive.org\/details\//, '');
    emit(TitleID, IA);
  }
}
