namespace.map:
function (doc) {
  if (doc.identifier) {
    for (var i in doc.identifier) {
      emit(doc.identifier[i].propertyID, doc.identifier[i].value);
    }
  }
}

doi.map:
function (doc) {
  if (doc.identifier) {
    for (var i in doc.identifier) {
      if (doc.identifier[i].propertyID == 'doi') {
        emit(doc.identifier[i].value, doc._id);
      }
    }
  }
}
