name-search-context.map:
function (doc) {
  if (doc._id.match(/^nametagged/)) {
    for (var page in doc.annotations) {
      var PageID = parseInt(doc.annotations[page].PageID);
      var ItemID = parseInt(doc.bhl_id);
      var year = 0;
      if (doc.datePublished) {
        year = parseInt(doc.datePublished);
      }
      for (var line in doc.annotations[page].annotations) {
        for (var i in doc.annotations[page].annotations[line]) {
          for (var j in doc.annotations[page].annotations[line][i].target.selector) {
            if (doc.annotations[page].annotations[line][i].target.selector[j].exact) {
              var selector = doc.annotations[page].annotations[line][i].target.selector[j];
              var searchTerm = selector.exact.toLowerCase();
              emit([searchTerm, ItemID, PageID, selector, year], 1);
            }
          }
        }
      }
    }
  }
}
