itemID-page.map:
function (doc) {
  if (doc._id.match(/^layout/)) {
    for (var i in doc.pages) {
      if (doc.pages[i].bhl_pageid) {
        emit(parseInt(doc.pages[i].bhl_pageid), [doc.bhl_item, parseInt(i)]);
      }
    }
  }
}

internetarchive.map:
function (doc) {
  if (doc._id.match(/^layout/)) {
    for (var i in doc.pages) {
      if (doc.pages[i].bhl_pageid && doc.pages[i].internetarchive) {
        var image = doc.pages[i].internetarchive;
        // strip any extension (which varies between hOCR and DjVu sources)
        image = image.replace(/\.(djvu|jp2)/, '');
        emit(parseInt(doc.pages[i].bhl_pageid), image);
      }
    }
  }
}

width-height.map:
function (doc) {
  if (doc._id.match(/^layout/)) {
    for (var i in doc.pages) {
      if (doc.pages[i].bhl_pageid) {
        emit(parseInt(doc.pages[i].bhl_pageid), [parseInt(doc.pages[i].image_bbox[2]), parseInt(doc.pages[i].image_bbox[3])]);
      }
    }
  }
}
