pageid.map:
function (doc) {
  if (doc._id.match(/^layout/)) {
    for (var i in doc.pages) {
      var lines = [];
      for (var j in doc.pages[i].text_lines) {
        lines.push(doc.pages[i].text_lines[j].text);
      }
      emit(parseInt(doc.pages[i].bhl_pageid), lines.join("\n"));
    }
  }
}
