about.map:
function (doc) {
  if (doc._id.match(/^bibliography/)) {
    var title = {};
    for(var i in doc) {
      switch (i) {
        case "_id":
          title['@id'] = doc._id;
          break;
        case "_rev":
        default:
          title[i] = doc[i];
          break;
      }
    }
    emit(doc._id, title);
  }
}

letter.map:
function (doc) {
  if (doc._id.match(/^bibliography/)) {
    var sort_name = doc.name;
    sort_name = sort_name.replace(/^The /, '');
    sort_name = sort_name.replace(/^./, sort_name[0].toUpperCase());
    emit ([sort_name.charAt(0), sort_name, doc._id], 1);
  }
}

items.map:
function (doc) {
  if (doc._id.match(/^item/)) {
    var item = {};
    for(var i in doc) {
      switch (i) {
        case "_id":
          item['@id'] = doc._id;
          break;
        case "_rev":
        case "identifier":
          break;
        default:
          item[i] = doc[i];
          break;
      }
    }
    emit(doc.isPartOf, item);
  }
}
